module.exports = {
	version: '1.0.0',
	test: function(expectVer) {
		var check = (this.version == expectVer ? true : false);
		log('invoke test from underscorek, this.version: ' + 
				this.version + (check ? '' : ' (expected: ' + expectVer + ')'), 
			  (check ? 'green' : 'red'),
				__filename);
	}
};

log('module underscorek loaded: ' + module.exports.version, 'reset', __filename);

